  .friend-grid{
    display:grid;grid-template-columns:repeat(auto-fill,minmax(240px,1fr));gap:16px
  }
  .friend-card{
    border-radius:14px;padding:14px 14px 12px;border:1px solid var(--c-bd);
    background:var(--c-bg);transition:transform .2s ease, box-shadow .2s ease, border-color .2s ease
  }
  .friend-card:hover{transform:translateY(-4px);box-shadow:0 10px 24px rgba(0,0,0,.08);border-color:var(--c-hi)}
  .friend-head{display:flex;align-items:center;gap:10px;margin-bottom:8px}
  .friend-avatar{
    width:44px;height:44px;border-radius:50%;object-fit:cover;border:2px solid var(--c-hi)
  }
  .friend-name{font-weight:700;font-size:16px;letter-spacing:.3px}
  .friend-desc{font-size:13px;opacity:.85;line-height:1.55;margin:6px 0 10px}
  .friend-tags{display:flex;flex-wrap:wrap;gap:6px;margin-top:6px}
  .friend-tag{
    font-size:12px;padding:3px 8px;border-radius:999px;border:1px dashed var(--c-hi);opacity:.95
  }
  .friend-link{
    display:inline-flex;align-items:center;gap:8px;margin-top:8px;font-size:13px;
    text-decoration:none;border-bottom:1px solid transparent
  }
  .friend-link:hover{border-color:var(--c-hi)}
  .friend-link svg{width:14px;height:14px}
  .divider{margin:24px 0;border-top:1px dashed var(--c-bd)}
  /* é¢œè‰²ï¼ˆè·Ÿéšäº®/æš—ï¼‰ */
  :root{
    --c-bg:rgba(255,255,255,.75);
    --c-bd:rgba(0,0,0,.12);
    --c-hi:#5B9BD5;
  }
  html.dark :root{
    --c-bg:rgba(20,24,35,.55);
    --c-bd:rgba(255,255,255,.16);
    --c-hi:#7EC8E3;
  }


