/* ===== About: Card UI (no Font Awesome) ===== */
:root {
  --about-gap: 14px;
  --about-radius: 16px;
  --about-shadow: 0 8px 24px rgba(0,0,0,.08);
  --about-shadow-hover: 0 12px 30px rgba(0,0,0,.12);
  --about-card-bg: rgba(255,255,255,.75);
  --about-text: #1f2937;
  --about-sub: #6b7280;
  --about-border: rgba(0,0,0,.06);
}
html.dark :root,
[data-theme="dark"] :root {
  --about-card-bg: rgba(24,24,27,.55);
  --about-text: #e5e7eb;
  --about-sub: #9ca3af;
  --about-border: rgba(255,255,255,.08);
}
.about-wrap {
  margin: 0 auto;
  max-width: 980px;
}
.about-hero {
  display: grid;
  grid-template-columns: 120px 1fr;
  gap: var(--about-gap);
  align-items: center;
  padding: 18px;
  border-radius: var(--about-radius);
  background: var(--about-card-bg);
  backdrop-filter: saturate(140%) blur(8px);
  box-shadow: var(--about-shadow);
  border: 1px solid var(--about-border);
}
.about-hero .avatar {
  width: 120px; height: 120px;
  border-radius: 50%;
  background: linear-gradient(135deg,#a78bfa, #60a5fa);
  display: grid; place-items: center;
  color: white; font-weight: 800; font-size: 40px;
  letter-spacing: 1px;
}
.about-hero .intro h1 { margin: 0 0 6px; font-size: 28px; color: var(--about-text); }
.about-hero .intro p { margin: 0; color: var(--about-sub); }

.about-grid {
  margin-top: 18px;
  display: grid;
  grid-template-columns: repeat(12, 1fr);
  gap: var(--about-gap);
}
.about-card {
  grid-column: span 12;
  padding: 16px 16px 14px;
  border-radius: var(--about-radius);
  background: var(--about-card-bg);
  border: 1px solid var(--about-border);
  box-shadow: var(--about-shadow);
  transition: transform .2s ease, box-shadow .2s ease;
}
.about-card:hover { transform: translateY(-2px); box-shadow: var(--about-shadow-hover); }
.about-card h3 { margin: 0 0 10px; font-size: 18px; color: var(--about-text); }
.about-card p { margin: 0 0 10px; color: var(--about-sub); }

/* 2-col / 3-col responsive */
@media (min-width: 720px) {
  .span-6 { grid-column: span 6; }
  .span-4 { grid-column: span 4; }
}
@media (min-width: 980px) {
  .span-6 { grid-column: span 6; }
  .span-4 { grid-column: span 4; }
}

/* profile chips */
.profiles { display: flex; flex-wrap: wrap; gap: 10px; }
.profile-chip {
  display: inline-flex; align-items: center; gap: 8px;
  padding: 10px 12px; border-radius: 12px;
  border: 1px dashed var(--about-border);
  text-decoration: none; color: var(--about-text);
  background: rgba(127,127,127,.04);
}
.profile-chip:hover { border-style: solid; }
.profile-chip svg { width: 20px; height: 20px; display: block; }

/* tags */
.tags { display:flex; flex-wrap:wrap; gap:8px; margin-top:8px; }
.tag { padding:6px 10px; border-radius: 999px; border:1px solid var(--about-border); font-size: 13px; color: var(--about-sub); }

/* timeline */
.timeline { margin: 0; padding-left: 12px; list-style: none; }
.timeline li { position: relative; padding-left: 16px; margin: 10px 0; color: var(--about-sub); }
.timeline li::before { content: ""; position: absolute; left: -6px; top: 6px; width: 8px; height: 8px; border-radius: 50%; background: linear-gradient(135deg,#60a5fa,#34d399); }

/* contact */
.contact { display:flex; flex-wrap:wrap; gap:10px; margin-top:6px; }
.button {
  display:inline-flex; align-items:center; justify-content:center; gap:8px;
  padding:10px 14px; border-radius:12px; border:1px solid var(--about-border);
  background: rgba(127,127,127,.04); text-decoration:none; color: var(--about-text);
}
.button:hover { transform: translateY(-1px); box-shadow: var(--about-shadow-hover); }
.button svg { width: 18px; height: 18px; }




